exports.cmd_example = function cmd_example() {
    'use strict';

    var cmd, cr;
    cmd = require('cmd');

    function myConsoleLog(callback) {
        console.log('Doin stuff');
        callback();
    }
    cr = new cmd.commandRegister.Synchronous('Bill');
    cr.on('command queue processed', function (name) {
        console.log('register ' + name + ' has finished');
    });
    cr.queue = [
        myConsoleLog,
        myConsoleLog,
        myConsoleLog,
        myConsoleLog,
        myConsoleLog,
        myConsoleLog
    ];
    
    cr.process();
};

