exports.exists_example = function exists_example() {
    'use strict';
    
    var Exists, exists, aDirectory, aFile, message;

    Exists    = require('Exists').Exists;

    exists    = new Exists();
    exists.on('error', function(err) {
        console.log(err);
    });

    aDirectory= '.';
    message   = 'Some specific message about the directory search';
    // returns true
    console.log(exists.dirMustExist(aDirectory, message));
    // logs the message to the console and emits error.
    exists.dirMustExist('wiggles', message);

    aFile     = './jakefile.js';
    message   = 'Some specific message about the file search';
    // returns true
    console.log(exists.fileMustExist(aFile, message));
    // logs the message to the console and emits error.
    exists.fileMustExist('wiggles', message);
};

