/*jslint indent: 4, maxerr: 50, white: true, node: true, stupid: true, sloppy: true */

/**
 * @file This file contains the template registry for our useage
 *  of mustache.
 * @author <a href="matthewkastor@gmail.com">Matthew Kastor</a>
 * @version 20121028
 * @requires fs
 * @exports templateManager
 */

var fs;

fs = require('fs');

/**
 * The template manager isn't intended to be run standalone. It is
 *  called using MustacheComb's this object and so is merged into it.
 */
function templateManager() {
    var my = this;

    /**
     *  Mustache Templates
     * @namespace
     */
    my.templates = {
        head     : fs.readFileSync('Jake/templates/head.mustache', 'utf8'),
        foot     : fs.readFileSync('Jake/templates/foot.mustache', 'utf8'),
        article  : fs.readFileSync('Jake/templates/article.mustache', 'utf8'),
        example  : fs.readFileSync('Jake/templates/example.mustache', 'utf8'),
        linksList: fs.readFileSync('Jake/templates/linksList.mustache', 'utf8'),
        includes : fs.readFileSync('Jake/templates/include.mustache', 'utf8')
    };
}

exports.templateManager = templateManager;