/*jslint indent: 4, maxerr: 50, white: true, node: true, stupid: true */

/**
 * @file This file contains the template registry for our useage
 *  of mustache.
 * @author <a href="matthewkastor@gmail.com">Matthew Kastor</a>
 * @version 20121030
 * @requires fs
 * @exports TemplateManager
 */

'use strict';

var fs, path;

fs = require('fs');
path = require('path');
/**
 * The template manager isn't intended to be run standalone. It is
 *  called using MustacheComb's this object and so is merged into it.
 * @class
 */
function TemplateManager() {
    var my = this;

    /**
     *  Mustache Templates
     * @namespace
     */
    my.templates = {
        head     : fs.readFileSync(path.resolve('Jake/templates/head.mustache'), 'utf8'),
        foot     : fs.readFileSync(path.resolve('Jake/templates/foot.mustache'), 'utf8'),
        article  : fs.readFileSync(path.resolve('Jake/templates/article.mustache'), 'utf8'),
        example  : fs.readFileSync(path.resolve('Jake/templates/example.mustache'), 'utf8'),
        linksList: fs.readFileSync(path.resolve('Jake/templates/linksList.mustache'), 'utf8'),
        includes : fs.readFileSync(path.resolve('Jake/templates/include.mustache'), 'utf8'),
        tags     : fs.readFileSync(path.resolve('Jake/templates/site/tags.mustache'), 'utf8')
    };
}

exports.TemplateManager = TemplateManager;